mapper.insert:

 INSERT INTO LETTNAUTHORINFO 
       ( AUTHOR_CODE
       , AUTHOR_NM
       , AUTHOR_DC
       , AUTHOR_CREAT_DE )
VALUES ( #{authorCode}
       , #{authorNm}
       , #{authorDc}
       , CURRENT_TIMESTAMP)

mapper.update:

UPDATE LETTNAUTHORINFO 
   SET AUTHOR_NM=#{authorNm}
     , AUTHOR_DC=#{authorDc}
 WHERE AUTHOR_CODE=#{authorCode}

mapper.select:

SELECT AUTHOR_CODE, AUTHOR_NM, AUTHOR_DC, AUTHOR_CREAT_DE
  FROM LETTNAUTHORINFO 
 WHERE AUTHOR_CODE=#{authorCode}
