mapper.select:
SELECT A.USER_ID, A.USER_NM, A.GROUP_ID, A.MBER_TY_CODE, (SELECT CODE_NM FROM LETTCCMMNDETAILCODE WHERE CODE_ID = 'COM012' AND CODE = A.MBER_TY_CODE AND USE_AT = 'Y') AS MBER_TY_NM, B.AUTHOR_CODE, (CASE WHEN B.SCRTY_DTRMN_TRGET_ID IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN, ESNTL_ID FROM (SELECT MBER_ID USER_ID, MBER_NM USER_NM, GROUP_ID, 'USR01' MBER_TY_CODE, ESNTL_ID FROM LETTNGNRLMBER UNION ALL SELECT ENTRPRS_MBER_ID USER_ID, CMPNY_NM USER_NM, GROUP_ID, 'USR02' MBER_TY_CODE, ESNTL_ID FROM LETTNENTRPRSMBER UNION ALL SELECT EMPLYR_ID USER_ID, USER_NM USER_NM, GROUP_ID, 'USR03' MBER_TY_CODE, ESNTL_ID FROM LETTNEMPLYRINFO ) A LEFT OUTER JOIN LETTNEMPLYRSCRTYESTBS B ON A.ESNTL_ID = B.SCRTY_DTRMN_TRGET_ID WHERE 1 = 1
AND A.USER_ID LIKE '%' || #{searchKeyword} || '%'
AND A.USER_NM LIKE '%' || #{searchKeyword} || '%'
AND A.GROUP_ID = #{searchKeyword}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

			UPDATE LETTNEMPLYRSCRTYESTBS 
			   SET MBER_TY_CODE=#{mberTyCode}
                 , AUTHOR_CODE=#{authorCode}
		  	 WHERE SCRTY_DTRMN_TRGET_ID=#{uniqId}
