mapper.insert:

			INSERT INTO LETTNROLEINFO 
				  ( ROLE_CODE
				  , ROLE_NM
                  , ROLE_PTTRN
                  , ROLE_DC
                  , ROLE_TY
				  , ROLE_SORT
				  , ROLE_CREAT_DE )
		   VALUES ( #{roleCode}
			      , #{roleNm}
				  , #{rolePtn}
                  , #{roleDc}
                  , #{roleTyp}
                  , #{roleSort}
				  , CURRENT_TIMESTAMP)

mapper.update:

			UPDATE LETTNROLEINFO 
			   SET ROLE_NM=#{roleNm}
                 , ROLE_PTTRN=#{rolePtn}
                 , ROLE_DC=#{roleDc}
                 , ROLE_TY=#{roleTyp}
                 , ROLE_SORT=#{roleSort}
				 , ROLE_CREAT_DE=CURRENT_TIMESTAMP
		  	 WHERE ROLE_CODE=#{roleCode}
