mapper.select:
SELECT CL_CODE , CL_CODE_NM , USE_AT FROM LETTCCMMNCLCODE WHERE 1 = 1
AND CL_CODE = #{searchKeyword}
AND CL_CODE_NM LIKE '%' || #{searchKeyword} || '%'
AND USE_AT = 'Y'
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM LETTCCMMNCLCODE WHERE 1 = 1
AND CL_CODE = #{searchKeyword}
AND CL_CODE_NM LIKE '%' || #{searchKeyword} || '%'

mapper.insert:

INSERT 
  INTO  LETTCCMMNCLCODE
     (  CL_CODE
     ,  CL_CODE_NM
     ,  CL_CODE_DC
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES  
     (  #{clCode}
     ,  #{clCodeNm}
     ,  #{clCodeDc}
     ,  #{useAt}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  LETTCCMMNCLCODE 
   SET  CL_CODE_NM        = #{clCodeNm}
     ,  CL_CODE_DC        = #{clCodeDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = CURRENT_TIMESTAMP
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CL_CODE           = #{clCode}

mapper.delete:

UPDATE  LETTCCMMNCLCODE 
   SET  USE_AT  = 'N'
 WHERE  CL_CODE = #{clCode}
