mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT A.CODE_ID , A.CODE , A.CODE_NM , A.USE_AT FROM LETTCCMMNDETAILCODE A , LETTCCMMNCODE B WHERE B.USE_AT = 'Y' AND A.CODE_ID = B.CODE_ID
AND A.CODE_ID = #{searchKeyword}
AND A.CODE LIKE '%' || #{searchKeyword} || '%'
AND A.CODE_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */ ) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT 
  INTO  LETTCCMMNDETAILCODE
     (  CODE_ID
     ,  CODE
     ,  CODE_NM
     ,  CODE_DC
     ,  USE_AT
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES  
     (  #{codeId}
     ,  #{code}
     ,  #{codeNm}
     ,  #{codeDc}
     ,  #{useAt}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  LETTCCMMNDETAILCODE 
   SET  CODE_NM           = #{codeNm}
     ,  CODE_DC           = #{codeDc}
     ,  USE_AT            = #{useAt}
     ,  LAST_UPDT_PNTTM = SYSDATETIME
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  CODE_ID   = #{codeId} 
   AND  CODE      = #{code}

mapper.delete:

UPDATE  LETTCCMMNDETAILCODE 
   SET  USE_AT    = 'N'
 WHERE  CODE_ID   = #{codeId} 
   AND  CODE      = #{code}
