mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM ( /* 구현 Sql */ SELECT ZIP , SN , CTPRVN_NM , SIGNGU_NM , EMD_NM , LI_BULD_NM , LNBR_DONG_HO FROM LETTCZIP WHERE 1 = 1
AND ZIP = #{searchKeyword}
AND CTPRVN_NM LIKE '%' || #{searchKeyword} || '%'
AND SIGNGU_NM LIKE '%' || #{searchKeyword} || '%'
AND EMD_NM LIKE '%' || #{searchKeyword} || '%'
AND LI_BULD_NM LIKE '%' || #{searchKeyword} || '%'
/* 구현 Sql */ ) ALL_LIST ) Z WHERE RNUM > #{firstIndex} AND RNUM <= #{firstIndex} + #{recordCountPerPage}

mapper.insert:

SELECT  NVL(MAX(SN)+1, 1) AS	sn
  FROM  LETTCZIP



INSERT 
  INTO  LETTCZIP
     (  ZIP         
     ,  SN          
     ,  CTPRVN_NM   
     ,  SIGNGU_NM   
     ,  EMD_NM      
     ,  LI_BULD_NM  
     ,  LNBR_DONG_HO
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES
     (  #{zip}       
     ,  #{sn}        
     ,  #{ctprvnNm}  
     ,  #{signguNm}  
     ,  #{emdNm}     
     ,  #{liBuldNm} 
     ,  #{lnbrDongHo}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     ,  SYSDATETIME
     ,  #{frstRegisterId}
     )


