mapper.update:

UPDATE  LETTCZIP 
   SET  CTPRVN_NM         = #{ctprvnNm}  
     ,  SIGNGU_NM         = #{signguNm}  
     ,  EMD_NM            = #{emdNm}     
     ,  LI_BULD_NM        = #{liBuldNm} 
     ,  LNBR_DONG_HO      = #{lnbrDongHo}
     ,  LAST_UPDT_PNTTM = SYSDATETIME
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  ZIP               = #{zip}
   AND  SN                = #{sn}
