mapper.insert:

SELECT  COALESCE(MAX(SN)+1, 1) AS	sn
  FROM  LETTCZIP



INSERT 
  INTO  LETTCZIP
     (  ZIP         
     ,  SN          
     ,  CTPRVN_NM   
     ,  SIGNGU_NM   
     ,  EMD_NM      
     ,  LI_BULD_NM  
     ,  LNBR_DONG_HO
     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES
     (  #{zip}       
     ,  #{sn}::integer
     ,  #{ctprvnNm}  
     ,  #{signguNm}  
     ,  #{emdNm}     
     ,  #{liBuldNm} 
     ,  #{lnbrDongHo}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     )


INSERT 
  INTO  LETTCZIP
     (  ZIP         
     ,  SN          
     ,  CTPRVN_NM   
     ,  SIGNGU_NM   
     ,  EMD_NM      
     ,  LI_BULD_NM  
