     ,  FRST_REGIST_PNTTM
     ,  FRST_REGISTER_ID
     ,  LAST_UPDT_PNTTM
     ,  LAST_UPDUSR_ID
     ) 
VALUES
     (  #{zip}       
     ,  #{sn}::integer
     ,  #{ctprvnNm}  
     ,  #{signguNm}  
     ,  #{emdNm}     
     ,  #{liBuldNm} 
     ,  #{lnbrDongHo}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     ,  CURRENT_TIMESTAMP
     ,  #{frstRegisterId}
     )

mapper.update:

UPDATE  LETTCZIP 
   SET  CTPRVN_NM         = #{ctprvnNm}  
     ,  SIGNGU_NM         = #{signguNm}  
     ,  EMD_NM            = #{emdNm}     
     ,  LI_BULD_NM        = #{liBuldNm} 
     ,  LNBR_DONG_HO      = #{lnbrDongHo}
     ,  LAST_UPDT_PNTTM = CURRENT_TIMESTAMP
     ,  LAST_UPDUSR_ID    = #{lastUpdusrId}
 WHERE  ZIP               = #{zip}
   AND  SN                = #{sn}::integer
