mapper.select:
SELECT a.LOG_ID , DATE_FORMAT(a.CREAT_DT, '%Y-%m-%d %H:%i') CREAT_DT , a.CONECT_MTHD , a.CONECT_IP , a.CONECT_ID , b.USER_NM AS LOGIN_NM FROM LETTNLOGINLOG a LEFT OUTER JOIN COMVNUSERMASTER b ON a.CONECT_ID = b.ESNTL_ID WHERE 1 = 1
AND a.CONECT_MTHD LIKE CONCAT ('%', #{searchWrd},'%')
AND DATE_FORMAT(a.CREAT_DT, '%Y%m%d') BETWEEN #{searchBgnDe} AND #{searchEndDe}
ORDER BY a.CREAT_DT DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.LOG_ID) as cnt FROM LETTNLOGINLOG a LEFT OUTER JOIN COMVNUSERMASTER b ON a.CONECT_ID = b.ESNTL_ID WHERE 1 = 1
AND a.CONECT_MTHD LIKE CONCAT ('%', #{searchWrd},'%')
AND DATE_FORMAT(a.CREAT_DT, '%Y%m%d') BETWEEN #{searchBgnDe} AND #{searchEndDe}
