mapper.insert:

INSERT INTO LETTNSYSLOG 
	( REQUST_ID
	  , SVC_NM
	  , METHOD_NM
	  , PROCESS_SE_CODE
	  , PROCESS_TIME
	  , RQESTER_ID
	  , RQESTER_IP
	  , OCCRRNC_DE )
VALUES ( #{requstId}
	  , #{srvcNm}
	  , #{methodNm}
	  , #{processSeCode}
	  , #{processTime}
	  , #{rqesterId}
	  , #{rqesterIp}
	  , to_char(CURRENT_TIMESTAMP, 'YYYYMMDD'))

mapper.select:
SELECT a.REQUST_ID , a.OCCRRNC_DE , a.SVC_NM , a.METHOD_NM , a.PROCESS_SE_CODE , c.CODE_NM AS PROCESS_SE_CODE_NM , a.PROCESS_TIME , a.RQESTER_IP , a.RQESTER_ID , b.USER_NM AS RQESTER_NM FROM LETTNSYSLOG a LEFT OUTER JOIN COMVNUSERMASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE c ON a.PROCESS_SE_CODE = c.CODE WHERE c.CODE_ID = 'COM033'
AND c.CODE_NM LIKE '%' || #{searchWrd} || '%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}
ORDER BY a.OCCRRNC_DE DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(a.REQUST_ID) as cnt FROM LETTNSYSLOG a LEFT OUTER JOIN COMVNUSERMASTER b ON a.RQESTER_ID = b.ESNTL_ID LEFT OUTER JOIN LETTCCMMNDETAILCODE c ON a.PROCESS_SE_CODE = c.CODE WHERE c.CODE_ID = 'COM033'
AND c.CODE_NM LIKE '%' || #{searchWrd} || '%'
AND a.OCCRRNC_DE BETWEEN #{searchBgnDe} AND #{searchEndDe}
