mapper.insert:

			INSERT INTO LETTHPROGRMCHANGEDTLS
				(  PROGRM_FILE_NM
              , REQUST_NO
              , RQESTER_ID
              , CHANGE_REQUST_CN
              , REQUST_DE
              , REQUST_SJ 
              ,PROCESS_STTUS_CODE)
			VALUES (  #{progrmFileNm}
                 , #{rqesterNo}
                 , #{rqesterPersonId}
                 , #{changerqesterCn}
                 , #{rqesterDe}
                 , #{rqesterSj} 
                 , 'A')

mapper.update:

			UPDATE LETTHPROGRMCHANGEDTLS
			SET RQESTER_ID         = #{rqesterPersonId} 
			   , CHANGE_REQUST_CN  = #{changerqesterCn}
            , REQUST_DE        = #{rqesterDe}       
            , REQUST_SJ        = #{rqesterSj} 
			WHERE PROGRM_FILE_NM=#{progrmFileNm}
			AND   REQUST_NO    =#{rqesterNo}

mapper.delete:

DELETE FROM LETTHPROGRMCHANGEDTLS
WHERE PROGRM_FILE_NM=#{progrmFileNm}
AND   REQUST_NO    =#{rqesterNo}

mapper.update:

			UPDATE LETTHPROGRMCHANGEDTLS
			SET REQUST_PROCESS_CN = #{rqesterProcessCn} 
           , OPETR_ID           = #{opetrId}         
           , PROCESS_STTUS_CODE = #{processSttus}    
           , PROCESS_DE         = #{processDe}       
			WHERE PROGRM_FILE_NM=#{progrmFileNm}
			AND   REQUST_NO    =#{rqesterNo}

mapper.select:
SELECT PROGRM_FILE_NM AS progrmFileNm , REQUST_NO AS rqesterNo , RQESTER_ID AS rqesterPersonId , CHANGE_REQUST_CN AS changerqesterCn , REQUST_PROCESS_CN AS rqesterProcessCn , OPETR_ID AS opetrId , PROCESS_STTUS_CODE AS processSttus , PROCESS_DE AS processDe , REQUST_DE AS rqesterDe , REQUST_SJ AS rqesterSj FROM LETTHPROGRMCHANGEDTLS WHERE 1=1
AND PROCESS_STTUS_CODE LIKE #{searchKeyword}
AND PROCESS_STTUS_CODE = #{searchKeyword}
AND REQUST_DE between #{searchKeywordFrom} and #{searchKeywordTo}
AND RQESTER_ID LIKE '%' || #{searchKeyword} || '%'
ORDER BY REQUST_NO ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) AS totcnt FROM LETTHPROGRMCHANGEDTLS WHERE 1=1
AND PROCESS_STTUS_CODE LIKE #{searchKeyword}
AND PROCESS_STTUS_CODE = #{searchKeyword}
AND REQUST_DE between #{searchKeywordFrom} and #{searchKeywordTo}
AND RQESTER_ID LIKE '%' || #{searchKeyword} || '%'
