mapper.select:
SELECT X.EMPLYR_ID, X.USER_NM, X.USER_ABSNCE_AT, X.REG_YN, X.LAST_UPDUSR_ID, X.LAST_UPDT_PNTTM FROM (SELECT A.EMPLYR_ID, A.USER_NM, (CASE WHEN B.USER_ABSNCE_AT IS NULL THEN 'N' ELSE B.USER_ABSNCE_AT END) AS USER_ABSNCE_AT, (CASE WHEN B.USER_ABSNCE_AT IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN, LAST_UPDUSR_ID, to_char(LAST_UPDT_PNTTM, 'YYYY-MM-DD') as LAST_UPDT_PNTTM FROM LETTNEMPLYRINFO A LEFT OUTER JOIN LETTNUSERABSNCE B ON A.EMPLYR_ID = B.EMPLYR_ID ) X WHERE 1 = 1
AND X.USER_NM LIKE '%' || #{searchKeyword} || '%'
AND X.USER_ABSNCE_AT = #{selAbsnceAt}
ORDER BY LAST_UPDT_PNTTM DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(*) totcnt FROM (SELECT A.EMPLYR_ID, A.USER_NM, (CASE WHEN B.USER_ABSNCE_AT IS NULL THEN 'N' ELSE B.USER_ABSNCE_AT END) AS USER_ABSNCE_AT, (CASE WHEN B.USER_ABSNCE_AT IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN, LAST_UPDUSR_ID, to_char(LAST_UPDT_PNTTM, 'YYYY-MM-DD') as LAST_UPDT_PNTTM FROM LETTNEMPLYRINFO A LEFT OUTER JOIN LETTNUSERABSNCE B ON A.EMPLYR_ID = B.EMPLYR_ID ) X WHERE 1 = 1
AND X.USER_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
AND X.USER_ABSNCE_AT = #{selAbsnceAt}
