mapper.select:
SELECT uniqId, userTy, userId , userNm, emailAdres, areaNo, middleTelno, endTelno, moblphonNo, groupId, sttus, sbscrbDe FROM( SELECT ESNTL_ID uniqId, 'USR03' userTy, EMPLYR_ID userId, USER_NM userNm, EMAIL_ADRES emailAdres, AREA_NO areaNo, HOUSE_MIDDLE_TELNO middleTelno, HOUSE_END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, EMPLYR_STTUS_CODE sttus, to_char(SBSCRB_DE, 'YYYY MM DD') as sbscrbDe FROM LETTNEMPLYRINFO ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'
ORDER BY sbscrbDe DESC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

SELECT COUNT(1) totcnt FROM( SELECT ESNTL_ID uniqId, 'USR03' userTy, EMPLYR_ID userId, USER_NM userNm, EMAIL_ADRES emailAdres, AREA_NO areaNo, HOUSE_MIDDLE_TELNO middleTelno, HOUSE_END_TELNO endTelno, MBTLNUM moblphonNo, GROUP_ID groupId, EMPLYR_STTUS_CODE sttus, to_char(SBSCRB_DE, 'YYYY MM DD') as sbscrbDe FROM LETTNEMPLYRINFO ) A WHERE 1=1
AND sttus LIKE #{sbscrbSttus}
AND userId LIKE #{searchKeyword}
AND userNm LIKE '%' #{searchKeyword} '%'

mapper.update:

UPDATE LETTNEMPLYRINFO 
